<?php    /**    * MakeXML    *    * @author Lin Jiong(slime09@gmail.com)    * @version v1.0    * @license Copyright (c) 2009 Lin Jiong (www.cn09.com)    * The LGPL (http://www.gnu.org/licenses/lgpl.html) licenses.    */    /*    * 从数组生成XML文件    */    class Xml    {		        function __construct(){		}				// 创建XML单项		function create_news_item($content, $url)		{			$item = "<item content=\"" . $content . "\" url=\"http://gx.2121.com/html/news/detail/" . $url . ".html\"></item>\n";			return $item;		}		// 创建XML单项		function create_graph($content, $xid)		{			$item = "<value xid=\"" . $xid . "\">" . $content . "</value>\n";			return $item;		}		// 创建XML单项		function create_item($title_data, $title_size, $content_data, $pubdate_data)		{			$item = "<series>\n";			$item .= "<title size=\"" . $title_size . "\">" . $title_data . "</title>\n";			$item .= "<content>" . $content_data . "</content>\n";			$item .= " <pubdate>" . $pubdate_data . "</pubdate>\n";			$item .= "</series>\n";			return $item;		}				// 创建XML单项		function create_slice($title_data, $content_data)		{			$item = "<slice title=\"" . $title_data . "\" >" . $content_data . "</slice>\n";			return $item;		}		/**		 * 将字串保存到文件		 * @param $fileName 文件名		 * @param $XMLString 已经生成的XML字串		 */		function saveToFile($fileName,$XMLString)		{			if(!$handle=fopen($fileName,'w'))			{				return FALSE;			}			if(!fwrite($handle,$XMLString))			{				return FALSE;			}			return TRUE;		}    }	?>